import React, { useState } from "react";
import Histogram from "../components/Histogram";
import ScatterPlot from "../components/ScatterPlot";
import StackedBarChart from "../components/StackedBarChart";
import BubbleChart from "../components/BubbleChart";
import RadarChart from "../components/RadarChart";

const options = [
  {
    value: "histogram",
    label: "Histogram of album release dates",
    component: <Histogram />,
  },
  {
    value: "scatter",
    label: "Scatter plot of song popularity vs. duration",
    component: <ScatterPlot />,
  },
  {
    value: "stacked",
    label: "Stacked bar chart showing the popularity of songs by each artist",
    component: <StackedBarChart />,
  },
  {
    value: "bubble",
    label:
      "Bubble chart showing the relationship between album release date, album popularity, and number of tracks",
    component: <BubbleChart />,
  },
  {
    value: "radar",
    label:
      "Radar chart showing the popularity of an artist's top 10 songs",
    component: <RadarChart />,
  },
];

const Visualizations = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleSelect = (event) => {
    const { value } = event.target;
    const option = options.find((opt) => opt.value === value);
    setSelectedOption(option);
  };

  return (
    <div style={{ backgroundColor: "#F2F2F2", padding: "2rem" }}>
      <h2 style={{ color: "#1DB954", marginBottom: "1rem" }}>
        Select Visualization
      </h2>
      <div style={{ display: "flex", alignItems: "center" }}>
        <label
          htmlFor="visualization"
          style={{ marginRight: "1rem", color: "#191414" }}
        >
          Choose a visualization:
        </label>
        <select
          id="visualization"
          name="visualization"
          value={selectedOption?.value || ""}
          onChange={handleSelect}
          style={{
            padding: "0.5rem",
            backgroundColor: "#FFF",
            color: "#191414",
            border: "none",
            boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
          }}
        >
          <option value="" disabled>
            Select an option
          </option>
          {options.map((opt) => (
            <option key={opt.value} value={opt.value}>
              {opt.label}
            </option>
          ))}
        </select>
      </div>
      <div style={{ marginTop: "2rem" }}>
        {selectedOption?.component}
      </div>
    </div>
  );
};

export default Visualizations;
