import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./SongList.css";
import axios from 'axios';

function formatDuration(milliseconds) {
	const minutes = Math.floor(milliseconds / 60000);
	const seconds = Math.floor((milliseconds % 60000) / 1000);
	return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
}


const SongList = () => {
    const [songs, setSongs] = useState([]);

	useEffect(() => {
		axios.get("https://cxvk7cmck9.execute-api.us-east-1.amazonaws.com/staging")
			.then(response => {
				console.log(response.data)
				const resp_songs = response.data
				const formattedSongs = resp_songs.map(song => ({
					...song,
					duration: formatDuration(song.duration),
				}));
				formattedSongs.forEach((item, i) => {
					item.id = i + 1;
				});
				setSongs(formattedSongs)

			})
			.catch(error => console.log(error));
	  }, []);

    const handleAddToFavorites = (id) => {
      const updatedSongs = songs.map((song) => {
        if (song.id === id) {
          return { ...song, isFavorite: true };
        }
        return song;
      });
      setSongs(updatedSongs);
    };

    const handleRemoveFromFavorites = (id) => {
      const updatedSongs = songs.map((song) => {
        if (song.id === id) {
          return { ...song, isFavorite: false };
        }
        return song;
      });
      setSongs(updatedSongs);
    };


    return (
        <div>
            <nav style={{ backgroundColor: "#1DB954", height: "50px" }}>
              <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", height: "100%" }}>
                <Link to="/" style={{ color: "#191414", textDecoration: "none", margin: "0 20px", fontSize: "24px" }}>
                  MySpotify
                </Link>
                <Link to="/visualizations" style={{ color: "#191414", textDecoration: "none", margin: "0 20px", fontSize: "20px" }}>
                  Visualizations
                </Link>
              </div>
            </nav>
            <div className="songlist-container">
                <h2>Most played songs this week</h2>
                <table className="songlist">
                    <thead>
                    <tr>
                        <th>Song Name</th>
                        <th>Artist Name</th>
						<th>Album Name</th>
                        <th>Song Duration</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {songs.map((song) => (
                        <tr key={song.id}>
                        <td>{song.song}</td>
                        <td>{song.artist}</td>
						<td>{song.album}</td>
                        <td>{song.duration}</td>
                        <td>
                            {song.isFavorite ? (
                            <>
                                <button
                                className="songlist-item-remove-from-favorites"
                                onClick={() => handleRemoveFromFavorites(song.id)}
                                >
                                Remove from Favorites
                                </button>
                            </>
                            ) : (
                            <button
                                className="songlist-item-add-to-favorites"
                                onClick={() => handleAddToFavorites(song.id)}
                            >
                                Add to Favorites
                            </button>
                            )}
                        </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default SongList;
