import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./SongList.css";

const SongList = () => {
    const [songs, setSongs] = useState([
      {
        id: 1,
        name: "Bohemian Rhapsody",
        artist: "Queen",
        duration: "6:07",
		album: "Queen",
        isFavorite: false,
      },
      {
        id: 2,
        name: "Stairway to Heaven",
        artist: "Led Zeppelin",
        duration: "8:02",
		album: "Led Zeppelin IV",
        isFavorite: false,
      },
      {
        id: 3,
        name: "Hotel California",
        artist: "Eagles",
        duration: "6:31",
		album: "Hotel California 2013",
        isFavorite: false,
      },
    ]);

	useEffect(() => {
		axios.get("https://oshfmkbl2c.execute-api.us-east-1.amazonaws.com/main")
		  .then(response => setSongs(response.data))
		  .catch(error => console.log(error));
	  }, []);

    const handleAddToFavorites = (id) => {
      const updatedSongs = songs.map((song) => {
        if (song.id === id) {
          return { ...song, isFavorite: true };
        }
        return song;
      });
      setSongs(updatedSongs);
    };

    const handleRemoveFromFavorites = (id) => {
      const updatedSongs = songs.map((song) => {
        if (song.id === id) {
          return { ...song, isFavorite: false };
        }
        return song;
      });
      setSongs(updatedSongs);
    };


    return (
        <div>
            <nav style={{ backgroundColor: "#1DB954", height: "50px" }}>
              <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", height: "100%" }}>
                <Link to="/" style={{ color: "#191414", textDecoration: "none", margin: "0 20px", fontSize: "24px" }}>
                  MySpotify
                </Link>
                <Link to="/visualizations" style={{ color: "#191414", textDecoration: "none", margin: "0 20px", fontSize: "20px" }}>
                  Visualizations
                </Link>
              </div>
            </nav>
            <div className="songlist-container">
                <h2>Most played songs this week</h2>
                <table className="songlist">
                    <thead>
                    <tr>
                        <th>Song Name</th>
                        <th>Artist Name</th>
						<th>Album Name</th>
                        <th>Song Duration</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {songs.map((song) => (
                        <tr key={song.id}>
                        <td>{song.name}</td>
                        <td>{song.artist}</td>
						<td>{song.album}</td>
                        <td>{song.duration}</td>
                        <td>
                            {song.isFavorite ? (
                            <>
                                <button
                                className="songlist-item-remove-from-favorites"
                                onClick={() => handleRemoveFromFavorites(song.id)}
                                >
                                Remove from Favorites
                                </button>
                            </>
                            ) : (
                            <button
                                className="songlist-item-add-to-favorites"
                                onClick={() => handleAddToFavorites(song.id)}
                            >
                                Add to Favorites
                            </button>
                            )}
                        </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default SongList;
